// CSS RESET

/* Box sizing rules */
*,
*::before,
*::after {
  box-sizing: border-box;
}

/* Remove default margin */
body,
h1,
h2,
h3,
h4,
p,
figure,
blockquote,
dl,
dd {
  margin: 0;
}

/* Remove list styles on ul, ol elements with a list role, which suggests default styling will be removed */
ul[role="list"],
ol[role="list"] {
  list-style: none;
}

/* Set core root defaults */
html:focus-within {
  scroll-behavior: smooth;
}

/* Set core body defaults */
body {
  min-height: 100vh;
  text-rendering: optimizeSpeed;
  line-height: 1.5;
}

/* A elements that don't have a class get default styles */
a:not([class]) {
  text-decoration-skip-ink: auto;
}

/* Make images easier to work with */
img,
picture {
  max-width: 100%;
  display: block;
}

/* Inherit fonts for inputs and buttons */
input,
button,
textarea,
select {
  font: inherit;
}

/* Remove all animations, transitions and smooth scroll for people that prefer not to see them */
@media (prefers-reduced-motion: reduce) {
  html:focus-within {
    scroll-behavior: auto;
  }

  *,
  *::before,
  *::after {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
    scroll-behavior: auto !important;
  }
}

// Google font import
@import url("https://fonts.googleapis.com/css2?family=Nunito:ital,wght@0,400;0,700;1,700&family=Open+Sans:ital,wght@0,300;0,400;0,600;0,700;1,300;1,400;1,600;1,700&display=swap");

// Custom variables

$font-header: "Nunito", sans-serif;
$font-main: "Open Sans", sans-serif;

$color-blue: #0082f3;
$color-text-main: rgb(92, 92, 92);
$color-nav: #333;

$color-button-blue-hover: #222;
$color-nav-blue-hover: #4d8cf5;

// General styles
body {
  font-family: "Open Sans", sans-serif;
  color: #5c5c5c;
  font-size: 0.9375rem;
  line-height: 1.375rem;
  font-weight: 400;
  background-color: #fff;
  overflow-x: hidden;
}

h1 {
  margin-top: 1.25rem;
  margin-bottom: 0.625rem;
  font-family: Nunito, sans-serif;
  color: #333;
  font-size: 2rem;
  line-height: 2.75rem;
  font-weight: 700;
}

h2 {
  margin-top: 1.25rem;
  margin-bottom: 0.625rem;
  font-family: Nunito, sans-serif;
  color: #333;
  font-size: 1.75rem;
  line-height: 2rem;
  font-weight: 700;
}

h3 {
  margin-top: 1.25rem;
  margin-bottom: 0.625rem;
  font-family: Nunito, sans-serif;
  color: #333;
  font-size: 1.25rem;
  line-height: 1.75rem;
  font-weight: 700;
}

h4 {
  margin-top: 0.625rem;
  margin-bottom: 0.3125rem;
  font-family: Nunito, sans-serif;
  color: #333;
  font-size: 1rem;
  line-height: 1.375rem;
  font-weight: 700;
}

p {
  margin-bottom: 1.25rem;
}

a {
  -webkit-transition: all 300ms ease-in-out;
  transition: all 300ms ease-in-out;
  color: #fff;
  font-weight: 600;
  text-decoration: none;
}

// Media query breakpoints
$mq-phone: 479px;
$mq-tablet: 767px;
$mq-desktop: 1023px;

// Mixins

@mixin flex-center {
  display: flex;
  justify-content: center;
  align-items: center;
}

// Utility classes
.primary-header {
  font-family: $font-header;
  color: #333;
  font-size: 1.75rem;
  line-height: 2rem;
  font-weight: 700;
  margin: 1.25rem auto 0.625rem;
  text-align: center;
  text-transform: uppercase;
}

.secondary-header {
  font-family: $font-main;
  font-weight: 400;
  font-size: 0.875rem;
  font-style: italic;
  text-align: center;
  line-height: 1.75rem;
  color: $color-text-main;
}

.uppercase {
  text-transform: uppercase;
}

.blue {
  background-color: $color-nav-blue-hover;
}

.white {
  background-color: #fff;
}

.align-center {
  text-align: center;
}

.button {
  display: inline-block;
  border: 0;
  text-transform: uppercase;
  cursor: pointer;
  text-decoration: none;
  line-height: inherit;
  text-align: center;
  border-radius: 0.3125em;
  margin: 0.625em;
  padding: 0.625em 1.875em;
  color: white;
}

.button-blue {
  background-color: $color-nav-blue-hover;
}

.button-blue:hover {
  background-color: $color-button-blue-hover;
}

.button-transparent {
  background-color: transparent;
  border: 1px solid $color-nav-blue-hover;
}

.button-transparent:hover {
  background-color: $color-nav-blue-hover;
}

// Sections

// Fixed Nav
.fixed-nav {
  display: none;
}

// Hero Section

.hero-section {
  background-image: linear-gradient(
      180deg,
      rgba(0, 0, 0, 0.75),
      rgba(0, 0, 0, 0.75)
    ),
    url(../images/hero.jpg);
  background-position: 0px 0px, 50% 50%;
  background-size: auto, cover;
  background-repeat: repeat, no-repeat;
  overflow: hidden;
  .hero-container {
    position: relative;
    min-height: clamp(770px, 100vh, 1100px);
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    padding-top: 1.8rem;
    .nav-container {
      position: absolute;
      top: 0;
      display: flex;
      justify-content: space-between;
      align-items: center;
      width: 100%;
      padding: 0.25rem;
      background-color: transparent;
      .logo-container {
        margin-inline: 1rem;
        padding: 1rem;
      }
      .nav-toggle {
        color: white;
        width: 52px;
        aspect-ratio: 1;
        margin: 1rem;
        padding: 1rem;
        @include flex-center;
        i {
          cursor: pointer;
        }
        &.active {
          background-color: $color-nav;
        }
        @media (min-width: $mq-desktop) {
          display: none;
        }
      }
      .desktop-nav {
        display: none;
        font-size: 0.9375rem;
        text-transform: uppercase;
        margin-right: 1rem;
        padding: 1.875rem 1.3rem;
        & > * {
          color: white;
          padding: 2rem 1rem;
          &:hover {
            color: $color-nav-blue-hover;
          }
        }
        @media (min-width: $mq-desktop) {
          display: flex;
          gap: 0.75rem;
        }
      }
      .mobile-nav {
        display: flex;
        justify-content: flex-start;
        align-items: flex-start;
        flex-direction: column;
        gap: 1rem;
        visibility: hidden;
        position: absolute;
        background-color: $color-nav;
        top: 100%;
        right: 1rem;
        width: calc(100% - 2rem);
        padding: 1rem;
        transform: translateY(-200%);
        transition: all 0.5s ease-in;
        overflow: hidden;
        z-index: 10000;
        a {
          color: white;
          margin: 1rem 1.75rem;
          text-transform: uppercase;
          font-weight: 600;
          font-size: 1rem;
          line-height: 1.5rem;
        }
        &.active {
          visibility: visible;
          transform: translateY(0%);
        }
      }
      @media (min-width: $mq-desktop) {
        width: 90%;
      }
    }
    // Hero content
    .hero-content {
      .hero-heading {
        color: white;
        margin: 1.25 auto 0.75rem;
        font-size: 2.25rem;
        line-height: 1.5;
        font-weight: 400;
        @media (min-width: $mq-tablet) {
          font-size: 2rem;
        }
        @media (min-width: $mq-desktop) {
          font-size: 3.625rem;
        }
      }
      .hero-text {
        width: min(90%, 760px);
        color: white;
        line-height: 1.75rem;
        margin: 0 auto 1.25rem;
        font-size: 0.83rem;
        @media (min-width: $mq-tablet) {
          font-size: 1rem;
        }
        @media (min-width: $mq-desktop) {
          font-size: 1.125rem;
        }
      }
      .button-container {
        @include flex-center;
        margin: 1rem auto 50px;
        padding: 1em;
        flex-direction: column;
        @media (min-width: $mq-phone) {
          flex-direction: row;
        }
      }
    }
    .arrow-container {
      position: absolute;
      bottom: 5px;
      font-size: 42px;
      i {
        color: white;
        transition: color 0.3s ease-in;
        &:hover {
          color: $color-nav-blue-hover;
        }
      }
    }
  }
}

// Add active class for sticky nav here
.nav-link.active-section {
  color: $color-nav-blue-hover;
}

// delete this after intersection observer works

section {
  min-width: 100vh;
  overflow: hidden;
}
