  margin-top: 1.25rem;
  margin-bottom: 0.625rem;
  font-family: Nunito, sans-serif;
  color: #333;
  font-size: 1.25rem;
  line-height: 1.75rem;
  font-weight: 700;
}

h4 {
  margin-top: 0.625rem;
  margin-bottom: 0.3125rem;
  font-family: Nunito, sans-serif;
  color: #333;
  font-size: 1rem;
  line-height: 1.375rem;
  font-weight: 700;
}

p {
  margin-bottom: 1.25rem;
}

a {
  -webkit-transition: all 300ms ease-in-out;
  transition: all 300ms ease-in-out;
  color: #fff;
  font-weight: 600;
  text-decoration: none;
}

// Media query breakpoints
$mq-phone: 479px;
$mq-tablet: 767px;
$mq-desktop: 1023px;

// Mixins

@mixin flex-center {
  display: flex;
  justify-content: center;
  align-items: center;
}

// Utility classes
.primary-header {
  font-family: $font-header;
  color: #333;
  font-size: 1.75rem;
  line-height: 2rem;
  font-weight: 700;
  margin: 1.25rem auto 0.625rem;
  text-align: center;
  text-transform: uppercase;
}

