  font-family: $font-main;
  font-weight: 400;
  font-size: 0.875rem;
  font-style: italic;
  text-align: center;
  line-height: 1.75rem;
  color: $color-text-main;
}

.uppercase {
  text-transform: uppercase;
}

.blue {
  background-color: $color-nav-blue-hover;
}

.white {
  background-color: #fff;
}

.align-center {
  text-align: center;
}

