  text-decoration: none;
  line-height: inherit;
  text-align: center;
  border-radius: 0.3125em;
  margin: 0.625em;
  padding: 0.625em 1.875em;
  font-weight: 600;
  transition: all 0.3s ease-in;
}

.button-blue {
  background-color: $color-nav-blue-hover;
  color: white;
}

.button-blue:hover {
  background-color: $color-button-blue-hover;
}

.button-transparent {
  background-color: transparent;
  border: 1px solid $color-nav-blue-hover;
  color: #222;
}

.button-transparent:hover {
  background-color: $color-nav-blue-hover;
  color: #fff;
}

