  margin-top: 2.5rem;
  margin-bottom: 1.25rem;
  h2 {
    font-size: 1.75rem;
  }
  h5 {
    font-size: 0.875rem;
    @media (min-width: $mq-tablet) {
      font-size: 1rem;
    }
    @media (min-width: $mq-desktop) {
      font-size: 1.125rem;
    }
  }
}

.div-line {
  width: 70px;
  height: 2px;
  margin: 1.25rem auto 0;
}

// Sections

// Fixed Nav
.fixed-nav {
  display: none;
}

// Hero Section

.hero-section {
  background-image: $bg-overlay, url(../images/hero.jpg);
  background-position: 0px 0px, 50% 50%;
  background-size: auto, cover;
  background-repeat: repeat, no-repeat;
  overflow: hidden;
  min-width: 100vw;
