      background-color: transparent;
      transition: all 0.4s ease-in;
      padding-top: 1rem;
      @media (min-width: $mq-desktop) {
        padding-inline: 2rem;
      }
      &.fixed-nav {
        position: fixed;
        background-color: $color-overlay;
        max-height: 100px;
      }
      .logo-container {
        margin-inline: 1rem;
        padding: 0 1rem;
      }
      .nav-toggle {
        color: white;
        width: 52px;
        aspect-ratio: 1;
        margin: 1rem;
        padding: 1rem;
        @include flex-center;
        i {
          cursor: pointer;
        }
        &.active {
          background-color: $color-nav;
        }
        @media (min-width: $mq-desktop) {
          display: none;
        }
      }
      .desktop-nav {
        display: none;
        font-size: 0.9375rem;
        text-transform: uppercase;
        margin-right: 1rem;
        padding: 1.875rem 1.3rem;
        & > * {
          color: white;
          padding: 2rem 1rem;
          &:hover {
            color: $color-nav-blue-hover;
          }
        }
        @media (min-width: $mq-desktop) {
          display: flex;
          gap: 0.75rem;
        }
      }
      .mobile-nav {
        display: flex;
        justify-content: flex-start;
        align-items: flex-start;
        flex-direction: column;
        gap: 1rem;
        visibility: hidden;
        position: absolute;
        background-color: $color-nav;
        top: 100%;
        right: 1rem;
        width: calc(100% - 2rem);
        padding: 1rem;
        transform: translateY(-200%);
        transition: all 0.5s ease-in;
        overflow: hidden;
        z-index: 10000;
        a {
          color: white;
          margin: 1rem 1.75rem;
          text-transform: uppercase;
          font-weight: 600;
          font-size: 1rem;
          line-height: 1.5rem;
        }
        &.active {
          visibility: visible;
          transform: translateY(0%);
        }
      }
    }
    // Hero content
    .hero-content {
      max-width: 80%;
      .hero-heading {
        color: white;
        margin: 1.25rem auto 0.75rem;
        text-align: center;
        font-size: 2.25rem;
        line-height: 1.5;
        font-weight: 400;
        @media (min-width: $mq-tablet) {
          font-size: 2rem;
        }
        @media (min-width: $mq-desktop) {
          font-size: 3.625rem;
        }
      }
      .hero-text {
        width: min(90%, 760px);
        color: white;
        line-height: 1.75rem;
        margin: 0 auto 1.25rem;
        font-size: 0.83rem;
        @media (min-width: $mq-tablet) {
          font-size: 1rem;
        }
        @media (min-width: $mq-desktop) {
          font-size: 1.125rem;
        }
      }
      .button-container {
        @include flex-center;
        margin: 1rem auto 50px;
        padding: 1em;
        flex-direction: column;
        .button {
          color: white;
        }
        @media (min-width: $mq-phone) {
          flex-direction: row;
        }
      }
    }
    .arrow-container {
      position: absolute;
      bottom: 5px;
      font-size: 42px;
      i {
        color: white;
        transition: color 0.3s ease-in;
        &:hover {
          color: $color-nav-blue-hover;
        }
      }
    }
  }
}

// Add active class for sticky nav here
.nav-link.active-link {
  color: $color-nav-blue-hover !important;
}

// about section

.about-section {
  display: grid;
  grid-template-columns: 1fr;
  margin: 0 1rem;
  padding: 0 1rem;
  @media (min-width: $mq-tablet) {
    grid-template-columns: 1fr 1fr;
    .about-header {
      grid-column: span 2;
    }
  }
}

// Portfolio section

.portfolio-section {
  display: flex;
  flex-direction: column;

  .grid {
    display: grid;
    grid-template-columns: 1fr 1fr;
    @media (min-width: $mq-desktop) {
      grid-template-columns: repeat(4, 1fr);
      position: relative;
    }
    .pf-item {
      position: relative;
      .overlay {
        position: absolute;
        top: 0;
        left: 0;
        right: 0;

        display: flex;
        justify-content: center;
        align-items: center;

        background-color: $color-overlay-portfolio;
        color: white;
        width: 100%;
        height: 100%;

        text-transform: uppercase;
        font-weight: 700;
        font-size: 1.25rem;
      }
    }
  }
  .cta-div {
    padding-block: 1.5rem;
    h3 {
      font-size: 1.125rem;
      @media (min-width: $mq-tablet) {
        font-size: 1.375rem;
      }
    }
  }
}

// Testimonials section

.testimonials-section {
  padding-block: 1rem;
  .overlay {
    background-image: $bg-overlay, url(../images/testimonials.jpg);
    background-position: 0px 0px, 50% 50%;
    background-size: auto, cover;
    background-repeat: repeat, no-repeat;
    width: 100vw;
    padding: 1rem;
    .section-header {
      color: white;
      h2 {
        color: inherit;
        margin: 1.25rem auto 0.75rem;
      }
    }
    .testimonial-items {
      position: relative;
      overflow-x: hidden;
      // border: 2px solid hotpink;
      width: 98vw;
      margin: auto;
      .wrapper {
        width: 300%;
        display: grid;
        grid-template-columns: repeat(3, 1fr);
        position: relative;
        transition: all 0.5s ease;
        transform: translateX(0);
        margin: 1rem auto;
        .testimonial-item {
          text-align: center;
          color: white;
          padding: 3rem;
          margin: 1rem auto;
          display: inline-block;

          font-size: 1rem;
          font-style: italic;
          line-height: 1.8;

          transition: all 0.5s ease-in-out;
          @media (min-width: $mq-tablet) {
            font-size: 1.2rem;
            span {
              font-size: 1rem;
            }
          }
          @media (min-width: $mq-desktop) {
            font-size: 1.4rem;
            span {
              font-size: 1.2rem;
            }
          }
          span {
            display: block;
            color: $color-nav-blue-hover;
            text-transform: uppercase;
            margin-top: 1rem;
            font-size: 0.875rem;
            font-weight: 700;
            font-style: normal;
          }
        }
      }
    }
    .circle-button-div {
      text-align: center;
      margin: 2rem;
      .circle {
        aspect-ratio: 1;
        width: 15px;
        border-radius: 50%;
        cursor: pointer;
        background-color: rgba(255, 255, 255, 0.4);
        border: none;

        transition: all 0.5s ease-in-out;
        &.active {
          background-color: rgba(255, 255, 255, 1);
        }
      }
    }
  }
}

// Services section

.services-section {
  margin-bottom: 2rem;
  @media (min-width: $mq-tablet) {
    .secondary-header {
      font-size: 1.125rem;
    }
  }
  .services-container {
    display: grid;
    grid-template-columns: 1fr;
    padding: 2rem;
    @media (min-width: $mq-tablet) {
      grid-template-columns: repeat(3, 1fr);
    }
    .services-item {
      padding: 1rem;
      i {
        color: $color-nav-blue-hover;
        font-size: 2rem;
      }
      h6 {
        margin: 20px auto 10px;
        font-size: 1.25rem;
        color: #333;
      }
      p {
        margin-bottom: 20px;
      }
    }
  }
}

// Team section

.team-section {
  .align-center {
    margin-bottom: 1.5rem;
  }
  .team-container {
    display: grid;
    gap: 1rem;
    grid-template-columns: 1fr;
    @media (min-width: $mq-phone) {
      grid-template-columns: 1fr 1fr;
    }
    @media (min-width: $mq-desktop) {
      grid-template-columns: repeat(4, 1fr);
    }
    .team-item {
      padding: 1rem;
    }
  }
}
